const express         = require('express')
const chalk           = require('chalk')
const path            = require('path')
const server          = express()
const PORT            = process.env.port || 5000
const PageRouter      = require('./router/pages-router')
const UserRouter      = require('./router/users-router')
const ServerMessage   = `AAMO App is running on localhost:${PORT}`
const db              = require('./utilities/db-connect')
const pub             = path.join(__dirname, '/public/build/')

server.use(express.static(pub))
server.use(express.json())

////////////////////////////////////////////////////////////////////////  API Router  

// Pages API Router
server.use(PageRouter)

// Users API Router
server.use(UserRouter)



server.get('/', (req, res)=>{
  res.status(200).sendFile('index.html')
})

server.listen(PORT, ()=>{
    return console.log(chalk.blue(ServerMessage))
})